import React, { useEffect, useState } from "react";
import DrinkDisplay from "./DrinkDisplay";
import ImageButtonDisplay from "./ImageButtonDisplay";

function CocktailGenerator() {
  const [search, setSearch] = useState("");
  const [drinks, setDrinks] = useState([]);
  const [selectedSpirit, setSelectedSpirit] = useState(null);

  const spirits = [
    {
        image: "/images/spirits/brandy.png",
        text: "brandy"
    },
    {
        image: "/images/spirits/cognac.png",
        text: "cognac"
    },
    {
        image: "/images/spirits/gin.png",
        text: "gin"
    },
    {
        image: "/images/spirits/rum.png",
        text: "rum"
    },
    {
        image: "/images/spirits/scotch.png",
        text: "scotch"
    },
    {
        image: "/images/spirits/tequila.png",
        text: "tequila"
    },
    {
        image: "/images/spirits/vodka.png",
        text: "vodka"
    },
    {
        image: "/images/spirits/whiskey.png",
        text: "whiskey"
    },
];

  useEffect(() => {
    fetch("/drinks")
      .then((res) => res.json())
      .then((data) => {
        setDrinks(data);
        // console.log(data);
      });
  }, []);

  let filter = drinks;
  if (search !== "") {
    filter = drinks.filter((drink) => {
      return drink.strDrink.toLowerCase().includes(search.toLowerCase());
    });
    filter.map((drink) => {
      return <DrinkDisplay key={drink.id} drink={drink} />;
    });
  }

  const drinkList = filter.map((drink) => {
    return <DrinkDisplay key={drink.id} drink={drink} />;
  });

  function handleChange(e) {
    const temp = e.target.value;
    setSearch(temp);
  }

  const handleCheckboxChange = (event, index) => {
    const { checked } = event.target;
    han
    if (checked) {
      // Set the selected spirit when a checkbox is checked
      setSelectedSpirit(index);
    } else {
      // Clear the selected spirit when a checkbox is unchecked
      setSelectedSpirit(null);
    }
  };


console.log(search)
  return (
    <div>
      <div>
        <img id="spirits-logo" src="/images/spirits/spirits-logo.png" />
      </div>
      <div className="spirit-buttons" >
      {spirits.map((spirit, index) => (
                <div key={index}>
                    <img id="spirit-buttons" src={spirit.image} />
                    <input
            className="spirit"
            type="checkbox"
            value={spirit.text}
            name="spirit"
            onChange={(event) => handleCheckboxChange(event, index) } // Pass the index to the handleChange function
            checked={selectedSpirit === index} // Check the checkbox if it's the selected one
          />
                </div>
            ))}
</div>
      {search && filter.length !== 0 ? (
        <div className="drink-list">{drinkList}</div>
      ) : (
        <div id="drink-search-padding"></div>
      )}
    </div>
  );
}

export default CocktailGenerator;
