import React from "react";
import FavoriteButton from "./FavoriteButton";

function DrinkDisplay({ drink, email }) {
  if (!drink) {
    return null;
  }

  return (
    <div>
      <br />
      <br />
      <div className="flex-container">
        <div className="drink-container">
          <h2 id="" >{drink.strDrink}</h2>

          <img id="drink-display-image" src={drink.strDrinkThumb} alt={drink.strDrink} />
          <h3 id="instructions">Instructions: </h3>
          <p>{drink.strInstructions}</p>

          <h3>Ingredients:</h3>
          <ul>
            {drink.drink_ingredient_associations.map((association) => (
              <li key={association.id}>{association.ingredient.name}</li>
            ))}
          </ul>

          <FavoriteButton drinkId={drink.id} email={email} />
          {drink.Category === "User created" ? 
          <div> <button onClick={() => handleDelete(drink.id)}>Delete</button>
            <button onClick={() => handleEdit(drink)}>Edit</button>
            {selectedDrink && selectedDrink.id === drink.id && (
              <div>
                <input
                  type="text"
                  placeholder="Drink Name"
                  value={updateData.strDrink}
                  onChange={(e) =>
                    setUpdateData({ ...updateData, strDrink: e.target.value })
                  }
                />
                <input
                  type="text"
                  placeholder="Image URL"
                  value={updateData.strDrinkThumb}
                  onChange={(e) =>
                    setUpdateData({
                      ...updateData,
                      strDrinkThumb: e.target.value,
                    })
                  }
                />
                {[1, 2, 3, 4, 5].map((i) => (
                  <input
                    type="text"
                    key={i}
                    placeholder={`Ingredient ${i}`}
                    value={updateData[`strIngredient${i}`]}
                    onChange={(e) =>
                      setUpdateData({
                        ...updateData,
                        [`strIngredient${i}`]: e.target.value,
                      })
                    }
                  />
                ))}
                <textarea
                  placeholder="Instructions"
                  value={updateData.strInstructions}
                  onChange={(e) =>
                    setUpdateData({
                      ...updateData,
                      strInstructions: e.target.value,
                    })
                  }
                />
                <button onClick={() => handleUpdate(selectedDrink.id)}>
                  Save
                </button>
                <button onClick={() => setSelectedDrink(null)}>Cancel</button>
              </div>
            )} </div> : <></> }
        </div>
      </div>
    </div>
  );
}

export default DrinkDisplay;
