import React from "react";
import { NavLink } from "react-router-dom";

function Home() {
  // const [drinks, setDrinks] = useState([]);

  // useEffect(() => {
  //   fetchSpecificDrinks();
  // }, []);

  // const fetchSpecificDrinks = async () => {
  //   const drinkIds = [11000, 11009, 11470, 11053];
  //   let specificDrinks = [];

  //   for (const drinkId of drinkIds) {
  //     const response = await fetch(`http://localhost:3000/drinks/${drinkId}`);

  //     if (response.ok) {
  //       const specificDrink = await response.json();
  //       specificDrinks.push(specificDrink);
  //     }
  //   }

  //   setDrinks(specificDrinks);
  // };

  return (
    <div>
      <div className="row-1">
        <div className="column">
          <img
            id="speech-trainer-main-logo"
            src={
              process.env.PUBLIC_URL + "/images/speech-trainer-main-logo.png"
            } 
            alt="speech-trainer-logo"
          />
        </div>
        <div className="column">
          <img
            id="home-kids-image-1"
            src={process.env.PUBLIC_URL + "/images/HomePageImages/Home4.png"}
            alt="kids"
          />
        </div>
        <div className="column">
          <div>
            <img
              id="home-level-up-image"
              src={process.env.PUBLIC_URL + "/images/HomePageImages/Home2.png"}
              alt="level-up"
            />

              <NavLink to={"/login"}>
                <img
                  id="login-image"
                  src={
                    process.env.PUBLIC_URL +
                    "/images/HomePageImages/HomeLogin.png"
                  }
                  alt="login"
                />
              </NavLink>

          </div>
        </div>
      </div>
      <div id="home-page-3">
        <div className="row-3">
          <div className="column-2">
            <NavLink to="/videos">
              <img
                id="video-speech-trainer-logo"
                src={process.env.PUBLIC_URL + "/images/homepage/Products7.png"}
                alt="Video Speech Trainer"
              />
            </NavLink>
          </div>
          <div className="column-2">
            <NavLink to="/speech-practice">
              <img
                id="first-words-logo"
                src={process.env.PUBLIC_URL + "/images/homepage/Products8.png"}
                alt="First Words"
              />
            </NavLink>
          </div>
          <div className="column-2">
            <NavLink to="/rock-paper-scissors-game">
              <img
                id="games-logo"
                src={process.env.PUBLIC_URL + "/images/homepage/Products9.png"}
                alt="Rock Paper Scissors"
              />
            </NavLink>
          </div>
        </div>
        <div className="row-4">
          <img
            id="home-learning-center-image"
            src={process.env.PUBLIC_URL + "/images/homepage/Products2.png"}
            alt="home-learning-center"
          />
        </div>
      </div>
    </div>
  );
}

export default Home;
