import React, { useEffect, useState } from "react";
import FavoriteButton from "./FavoriteButton";
import DrinkEdit from "./DrinkEdit";

function DrinkDisplay({ drink, email, fetchUserDrinks, drinks, setDrinks }) {
  const [currentDrink, setCurrentDrink] = useState(drink)
  const [updateData, setUpdateData] = useState({
    strDrink: "",
    strDrinkThumb: "",
    strIngredient1: "",
    strIngredient2: "",
    strIngredient3: "",
    strIngredient4: "",
    strIngredient5: "",
    strInstructions: "",
  });
  if (!drink) {
    console.log("null");
    return null;
  }
  console.log(drink)
  return (
    <div>
      <br />
      <br />
      <div className="flex-container">
        <div className="drink-container">
          <h2 id="">{currentDrink.strDrink}</h2>

          <img
            id="drink-display-image"
            src={currentrink.strDrinkThumb}
            alt={drink.strDrink}
          />
          <h3 id="instructions">Instructions: </h3>
          <p>{drink.strInstructions}</p>

          <h3>Ingredients:</h3>
          <ul>
            {drink.drink_ingredient_associations.map((association) => (
              <li key={association.id}>{association.ingredient.name}</li>
            ))}
          </ul>

          <FavoriteButton drinkId={drink.id} email={email} />
          <DrinkEdit drink={drink} updateData={updateData} setUpdateData={setUpdateData} fetchUserDrinks={fetchUserDrinks} setDrinks={setDrinks} />
        </div>
      </div>
    </div>
  );
}

export default DrinkDisplay;
