  const [search, setSearch] = useState("");
  const [drinks, setDrinks] = useState([]);


  useEffect(() => {
    fetch("/drinks")
      .then((res) => res.json())
      .then((data) => {
        setDrinks(data);
        console.log(data);
      });
  }, []);

  let filter = drinks;
  if (search !== "") {
    filter = drinks.filter((drink) => {
      return drink.strDrink.toLowerCase().includes(search.toLowerCase());
    });
