import React from "react";


function EmailPopup() {



  return (
    <div className="email-popup"> 
      <img
        src="/images/emailpopup/email-popup.png"
        id="email-popup-image" 
        alt="Email Popup"
      />
      <img
        src="/images/emailpopup/email-popup-button.png"
        id="email-signup-button" 
        alt="Email Signup Button"
      />

<h3 id="new-drink-text">Fill Out Form to Create a Drink</h3>
        <div id="create-drink-container">
          <form onSubmit={formik.handleSubmit} className="new-drink-form">
            <input
              id="drinkName"
              name="drinkName"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.drinkName}
              placeholder="Drink Name"
            />
            {formik.values.ingredients.map((ingredient, index) => (
              <input
                key={index}
                id={`ingredients[${index}]`}
                name={`ingredients[${index}]`}
                type="text"
                onChange={formik.handleChange}
                value={formik.values.ingredients[index]}
                placeholder={`Ingredient ${index + 1}`}
              />
            ))}

            <input
              id="imageURL"
              name="imageURL"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.imageURL}
              placeholder="Image URL"
            />
            <textarea
              id="instructions"
              name="instructions"
              onChange={formik.handleChange}
              value={formik.values.instructions}
              placeholder="Instructions"
              rows={5}
            />
            {formErrors.length > 0
              ? formErrors.map((err, index) => (
                  <p key={index} style={{ color: "red" }}>
                    {err}
                  </p>
                ))
              : null}
            <input type="submit" value="Add Drink" />
          </form>
    </div>
    </div>
  );
}

export default EmailPopup;
