import React, { useEffect, useState } from "react";
import DrinkDisplay from "./DrinkDisplay";
import ImageButtonDisplay from "./ImageButtonDisplay";

function CocktailGenerator() {
  const [search, setSearch] = useState("");
  const [drinks, setDrinks] = useState([]);

  function handleFilterDrinks(buttonId) {
    let filter = drinks;
    if (search !== "") {
      filter = drinks.filter((drink) => {
        return drink.strDrink.toLowerCase().includes(search.toLowerCase());
      });
      filter.map((drink) => {
        return <DrinkDisplay key={drink.id} drink={drink} />;
      });
    }
  }

  return (
    <div>
      <ImageButtonDisplay />
      <div>{handleFilterDrinks}</div>
    </div>
  );
}

export default CocktailGenerator;
